import React, {useState, useEffect} from 'react';
import styles from './logo.module.css';

import react from './assets/react.svg';
import laravel from './assets/laravel.png';
import js from './assets/js.png';
import css from './assets/css.png';
import mysql from './assets/mysql.png';
import docker from './assets/docker.png';
import nodejs from './assets/nodejs.png';
import { IconsProvider, IconsContext } from '../../contexts/iconscontext';

const Logo = props => {

  const { icon, width } = props;
  const { icons } = IconsContext;


  const big = styles.__img_post;
  const small = styles.__img_postcard;
  return(
    <IconsProvider>

    icons.map(
    item =>
      item.id == icon
        ? <img width={width} src={item.url} alt="hola"/>
        : <div />
        )
    </IconsProvider>
  )
};
export default Logo;
