import React, { useEffect, useState } from 'react';
import Navbar from '../../components/navbar/navbar';
import styles from './postcardview.module.css';
import PostCard from '../../components/postcard/postcard2';
import { Link } from 'react-router-dom';
import { POST, POSTBYID } from '../../routes/routes';
import react from '../../components/postcard/assets/react.png';
import laravel from '../../components/postcard/assets/laravel.png';
import js from '../../components/postcard/assets/js.png';
import css from '../../components/postcard/assets/css.png';
import docker from '../../components/postcard/assets/docker.png';
import php from '../../components/postcard/assets/php.png';
import sql from '../../components/postcard/assets/sql.png';


const PostcardView = () => {

  const [posts, setPosts] = useState(['']);
  console.log(sql)

  const getPost = () => {
    const url = 'http://localhost/api/posts';
    const options = {
        method: 'GET',
        headers: new Headers(),
    };
    fetch(url, options)
        .then(response => {
            if (response.status === 200) {
                return response.json();
            }
            return Promise.reject(response.status);
        })
        .then(function (myJson) {
          setPosts(myJson);
          console.log(posts);
        })
        .catch(error => console.log(error));
  }

  useEffect(() => {
    getPost();
  }, [])

  return (
    <>

      <div className={styles.__container_postcardview}>
        <div className={styles.__postcardview_title_div}>
          <h1 className={styles.__postcardview_title}>Esta es la postcardview</h1>
        </div>
        <div className={styles.__main_postcardview}>
          {
            posts.map((item) => 

              <Link to={POSTBYID+item.id}>
                <PostCard group={item.gr} name={item.title} description={item.description} />
              </Link>

              )
            }
        </div>
      </div>
    </>
  );
};

export default PostcardView;
