import React, { useState, useEffect } from 'react';
import styles from './post.module.css';
import react from '../../components/postcard/assets/react.svg';
import laravel from '../../components/postcard/assets/laravel.png';
import js from '../../components/postcard/assets/js.png';
import css from '../../components/postcard/assets/css.png';
import docker from '../../components/postcard/assets/docker.png';
import php from '../../components/postcard/assets/php.png';

const Post = (props) => {

  const [post, setPost] = useState({});


  const getPost = () => {
    const url = 'http://localhost/api/posts/${id}';
    const options = {
        method: 'GET',
        headers: new Headers(),
    };
    fetch(url, options)
        .then(response => {
            if (response.status === 200) {
                return response.json();
            }
            return Promise.reject(response.status);
        })
        .then(function (myJson) {
          setPost(myJson);
          console.log(post);
        })
        .catch(error => console.log(error));
  }

  useEffect(() => {
    getPost();
  }, [])



  return (
    <div className={styles.__post_div}>
          <div className={styles.__post_header}>
        <img className={styles.__img_post} src={post.icon_id} alt="react" />
        <h3>
          {post.title}
        </h3>
          </div>
          <div className={styles.__post_content}>
        <p>
            {post.content}
              </p>
          </div>
    </div>
  );
};
export default Post;
