import React, { useState, useEffect } from 'react';
import styles from './postcard.module.css';
import react from './assets/react.svg';
import laravel from './assets/laravel.png';
import js from './assets/js.png';
import css from './assets/css.png';
import mysql from './assets/mysql.png';
import docker from './assets/docker.png';


const PostCard = props => {

  const { iconId, name, description } = props;
  console.log(iconId);
    const [icons, setIcons] = useState(['']);
    const iconArray = [0, react, laravel, mysql];


   const getIcon = () => {
    const url = "http://localhost/api/icons";
    const options = {
        method: 'GET',
        headers: new Headers(),
    };
     fetch(url, options)
        .then(  response => {
            if (response.status === 200) {
                return  response.json();
            }
            return Promise.reject(response.status);
        })
        .then(function (myJson) {
          setIcons(myJson);
        })
        .catch(error => console.log(error));
   }

useEffect(() => {
  getIcon();
  console.log(iconId);
}, [])




    return (
            icons.map((item) => 
                 item.id == iconId ?
                    <div className={styles.__postcard_div}>
                        <div className={styles.__card_header}>
                            <img className={styles.__img_post} src={iconArray[{iconId}]} alt={item.name} />
                            <h4>{name}</h4>
                        </div>

                        <div className={styles.__card_content}>
                            <p>
                                {description}
                            </p>
                        </div>
                    </div>
                    :
                    <div></div>

        )
    )


};
export default PostCard;
