import React, { useState } from 'react';
import styles from './postcard.module.css';
import react from './assets/react.svg';
import laravel from './assets/laravel.png';
import js from './assets/js.png';
import css from './assets/css.png';
import docker from './assets/docker.png';
import php from './assets/php.png';

const PostCard = props => {

  const { iconId, name, description } = props;

  const [iconUrl, setIconUrl] = useState();


  const getIcon = (id) => {
    const url = `http://localhost/api/icons/${id}`;
    const options = {
        method: 'GET',
        headers: new Headers(),
    };
    fetch(url, options)
        .then(response => {
            if (response.status === 200) {
                return response.json();
            }
            return Promise.reject(response.status);
        })
        .then(function (myJson) {
          setIconUrl(myJson);
          console.log(iconUrl);
        })
        .catch(error => console.log(error));
  }

  getIcon(iconId);


  return (
    <div className={styles.__postcard_div}>
      <div className={styles.__card_header}>
        <img className={styles.__img_post} src={iconUrl} alt={react} />
        <h4>{name}</h4>
      </div>

      <div className={styles.__card_content}>
        <p>
          {description}
        </p>
      </div>
    </div>
  );
};
export default PostCard;
