import styles from './postformview.module.css';


const PostForm = () => {


    const [data, setData] = useState({})

    const handleInputChange = (event) => {
        setData({
            ...data,
            [event.target.name]: event.target.value
        })
        console.log(data);
    }


    return (
        <div>
            <h1>Form Post View</h1>
            <div className={styles.__div_form}>
            <input  className={styles.__input_form} name="title" type="text" placeholder="title"/>
            <input  className={styles.__input_form} name="description" type="text" placeholder="description" />
            <input className={styles.__input_form} name="content" type="text" placeholder="content" />
            <input  className={styles.__input_form} name="icon" type="number"  placeholder="1" />
            <input  className={styles.__input_form} name="button" type="button"  value="Crear Post" />

            </div>

        </div>
    )
}
export default PostForm;
